mapper.sql:
FROM book a
WHERE 1=1
	AND bookDelNy = #{searchDelNy}

		AND bookSeq = #{searchValue}
		AND name LIKE CONCAT('%',#{searchValue},'%')
		AND publisher LIKE CONCAT('%',#{searchValue},'%')
		AND cost = #{searchValue}

mapper.insert:
INSERT INTO book(
	sign
	,name
	,subName
	,publisher
	,dop
	,cost
	,sale
	,accmulate
	,isbn
	,page
	,bookSize
	,topic
	,introduce
	,image
	,list
	,content
	,rop
	,stock
	,amount
	,bookDelNy
)
VALUES(
	#{sign}
	,#{name}
	,#{subName}
	,#{publisher}
	,#{dop}
	,#{cost}
	,#{sale}
	,#{accmulate}
	,#{isbn}
	,#{page}
	,#{bookSize}
	,#{topic}
	,#{introduce}
	,#{image}
	,#{list}
	,#{content}
	,#{rop}
	,#{stock}
	,0
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE book
SET
	sign = #{sign}
	,name = #{name}
	,subName = #{subName}
	,publisher = #{publisher}
	,dop = #{dop}
	,cost = #{cost}
	,sale = #{sale}
	,accmulate = #{accmulate}
	,isbn = #{isbn}
	,page = #{page}
	,bookSize = #{size}
	,topic = #{topic}
	,introduce = #{introduce}
	,image = #{image}
	,list = #{list}
	,content = #{content}
	,rop = #{rop}
	,stock = #{stock}
	,amount = #{amount}
WHERE 1=1
	AND	bookSeq = #{bookSeq}

mapper.insert:
INSERT INTO book_comment(
