mapper.select:
select
	count(*)
from favorite
where 1=1
and book_bookSeq = #{bookSeq}
and member_memberSeq = #{memberSeq}

mapper.insert:
INSERT INTO favorite(
	member_memberSeq
	,book_bookSeq
)
VALUES(
	#{memberSeq} 
	,#{bookSeq}
)

	SELECT last_insert_id()

mapper.delete:
DELETE 
from favorite
where 1=1
and book_bookSeq = #{bookSeq}
and member_memberSeq = #{memberSeq}
