mapper.sql:
FROM book a
WHERE 1=1
	AND delNy = #{searchDelNy}

		AND bookSeq = #{searchValue}
		AND name LIKE CONCAT('%',#{searchValue},'%')
		AND publisher LIKE CONCAT('%',#{searchValue},'%')
		AND cost = #{searchValue}

mapper.insert:
INSERT INTO CG(
	sign
	,name
	,subName
	,publisher
	,dop
	,dor
	,cost
	,sale
	,accmulate
	,isbn
	,page
	,size
	,introduce
	,image
	,list
	,content
	,rop
	,stock
	,amount
)
VALUES(
	#{sign}
	,#{name}
	,#{subName}
	,#{publisher}
	,#{dop}
	,#{dor}
	,#{cost}
	,#{sale}
	,#{accmulate}
	,#{isbn}
	,#{page}
	,#{size}
	,#{introduce}
	,#{image}
	,#{list}
	,#{content}
	,#{rop}
	,#{stock}
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE CG
SET
	sign = #{sign}
	,name = #{name}
	,subName = #{subName}
	,publisher = #{publisher}
	,dop = #{dop}
	,dor = #{dor}
	,cost = #{cost}
	,sale = #{sale}
	,accmulate = #{accmulate}
	,isbn = #{isbn}
	,page = #{page}
	,size = #{size}
	,introduce = #{introduce}
	,image = #{image}
	,list = #{list}
	,content = #{content}
	,rop = #{rop}
	,stock = #{stock}
	,amount = #{amount}
WHERE 1=1
	AND	bookSeq = #{bookSeq}
