mapper.insert:
INSERT INTO CG(
	CCG_CGSeq
	,CNameKor
	,CNameEng
	,CUseNy
	,CDelNy
	,COrder
	,CRegistration
)
VALUES(
	#{CCG_CGSeq}
	,#{CNameKor}
	,#{CNameEng}
	,#{CUseNy}
	,0
	,#{COrder}
	,#{CRegistration}
)

	SELECT last_insert_id()

mapper.update:
UPDATE CG
SET
	CNameKor = #{CNameKor}
	,CNameEng = #{CNameEng}
	,CUseNy = #{CUseNy}
	,COrder = #{COrder}
	,CDelNy = #{CDelNy}
	,CRegistration = #{CRegistration}
WHERE 1=1
	AND	CSeq = #{CSeq}

mapper.sql:
FROM CG a
JOIN CCG b on a.CCG_CGSeq = b.CGSeq
WHERE 1=1
	AND CDelNy = #{searchDelNy}

		AND CCG_CGSeq = #{searchValue}
		AND b.CGNameKor LIKE CONCAT('%',#{searchValue},'%')
		AND CSeq = #{searchValue}
		AND CNameKor LIKE CONCAT('%',#{searchValue},'%')
		AND CNameEng LIKE CONCAT('%',#{searchValue},'%')
