mapper.insert:
INSERT INTO member (
	id
	,password
	,name
	,userName
	,gender
	,dob
	,phone
	,email
	,emailStart
	,emailEnd
	,emailLast
	,zip
	,address1
	,address2
	,privacy
	,email_ctr
	,kakao_ctr
	,sms_ctr
	,accmulate
	,memberDelNy
	,memberUseNy
	,registration
)
VALUES (
	#{id}
	,#{password}
	,#{name}
	,#{userName}
	,#{gender}
	,#{dob}
	,#{phone}
	,#{email}
	,#{emailStart}
	,#{emailEnd}
	,#{emailLast}
	,#{zip}
	,#{address1}
	,#{address2}
	,#{privacy}
	,#{email_ctr}
	,#{kakao_ctr}
	,#{sms_ctr}
	,0
	,0
	,1
	,#{registration}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	id = #{id}
	,password = #{password}
	,name = #{name}
	,userName = #{userName}
	,gender = #{gender}
	,dob = #{dob}
	,phone = #{phone}
	,email = #{email}
	,emailStart = #{emailStart}
	,emailEnd = #{emailEnd}
	,emailLast = #{emailLast}
	,zip = #{zip}
	,address1 = #{address1}
	,address2 = #{address2}
	,privacy = #{privacy}
	,email_ctr = #{email_ctr}
	,kakao_ctr = #{kakao_ctr}
	,sms_ctr = #{sms_ctr}
	,accmulate = #{accmulate}
	,memberDelNy = #{memberDelNy}
	,memberUseNy = #{memberUseNy}
	,registration = #{registration}

WHERE 1=1
	AND	memberSeq = #{memberSeq}

UPDATE member
	SET
		userName = #{userName}
		,phone = #{phone}
		,email = #{email}
		,emailStart = #{emailStart}
		,emailEnd = #{emailEnd}
		,emailLast = #{emailLast}
		,zip = #{zip}
		,address1 = #{address1}
		,address2 = #{address2}
		,privacy = #{privacy}
		,email_ctr = #{email_ctr}
		,kakao_ctr = #{kakao_ctr}
		,sms_ctr = #{sms_ctr}
	WHERE 1=1
		AND	memberSeq = #{memberSeq}

mapper.sql:
FROM member a
WHERE 1=1

	AND memberDelNy = #{searchDelNy}
	AND gender = #{searchGender}
	AND privacy = #{searchPrivacy}

		AND memberSeq = #{searchValue}
		AND name LIKE CONCAT('%',#{searchValue},'%')
		AND id LIKE CONCAT('%',#{searchValue},'%')
		AND userName LIKE CONCAT('%',#{searchValue},'%')
		AND email LIKE CONCAT('%',#{searchValue},'%')

mapper.select:
SELECT
	 COUNT(*) 
FROM member a
WHERE 1=1
	AND id = #{id}
	AND password = #{password}
