mapper.select:
SELECT
	a.*
FROM member a
WHERE 1=1

	AND memberDelNy = #{searchDelNy}
	AND gender = #{searchGender}
	AND privacy = #{searchPrivacy}

		AND memberSeq = #{searchValue}
		AND name LIKE CONCAT('%',#{searchValue},'%')
		AND id LIKE CONCAT('%',#{searchValue},'%')
		AND userName LIKE CONCAT('%',#{searchValue},'%')
		AND email LIKE CONCAT('%',#{searchValue},'%')

mapper.insert:
INSERT INTO member (
	id
	,password
	,name
	,userName
	,gender
	,dob
	,phone
	,number
	,email
	,zip
	,address1
	,address2
	,privacy
	,email_ctr
	,kakao_ctr
	,sms_ctr
	,accmulate
	,memberDelNy
)
VALUES (
	#{MId}
	,#{MPassword}
	,#{MName}
	,#{MUserName}
	,#{MGender}
	,#{MDob}
	,#{MPhone}
	,#{MNumber}
	,#{MEmail}
	,#{MZip}
	,#{MAddress1}
	,#{MAddress2}
	,#{MPrivacy}
	,#{MEmail_ctr}
	,#{MKakao_ctr}
	,#{MSms_ctr}
	,0
	,0
)

	SELECT last_insert_id()
