mapper.select:
SELECT
	a.*
FROM member a
WHERE 1=1

	AND memberDelNy = #{searchDelNy}
	AND gender = #{searchGender}
	AND privacy = #{searchPrivacy}

		AND memberSeq = #{searchValue}
		AND name LIKE CONCAT('%',#{searchValue},'%')
		AND id LIKE CONCAT('%',#{searchValue},'%')
		AND userName LIKE CONCAT('%',#{searchValue},'%')
		AND email LIKE CONCAT('%',#{searchValue},'%')
