mapper.sql:
FROM book a
JOIN book_writer b on b.book_bookSeq = a.bookSeq 
WHERE 1=1
	AND delNy = #{searchDelNy}

		AND bookSeq = #{searchValue}
		AND name LIKE CONCAT('%',#{searchValue},'%')
		AND publisher LIKE CONCAT('%',#{searchValue},'%')
		AND cost = #{searchValue}

mapper.insert:
INSERT INTO book(
	member_memberSeq
	,transport_transportSeq
	,payment
	,time
	,purchaseStatus
	,delNy
)
VALUES(
	#{member_memberSeq}
	,#{transport_transportSeq}
	,#{payment}
	,#{time}
	,#{purchaseStatus}
	,#{delNy}
	,0
)

	SELECT last_insert_id()
