mapper.insert:
INSERT INTO transport (
	member_memberSeq
	,transportDiv
	,phone
	,home
	,zip
	,address1
	,address2
	,lag
	,lat
	,useNy
	,delNy
	,registration
)
VALUES (

	#{member_memberSeq}
	,#{transportDiv}
	,#{phone}
	,#{home}
	,#{zip}
	,#{address1}
	,#{address2}
	,#{lag}
	,#{lat}
	,1
	,0
	,#{registration}

)

	SELECT last_insert_id()

mapper.update:
UPDATE transport
SET
	member_memberSeq = #{member_memberSeq}
	,transportDiv = #{transportDiv}
	,phone = #{phone}
	,home = #{home}
	,zip = #{zip}
	,address1 = #{address1}
	,address2 = #{address2}
	,lag = #{lag}
	,lat = #{lat}
	,useNy = #{useNy}
	,delNy = #{delNy}
	,registration = #{registration}
WHERE 1=1
	AND transportSeq = #{transportSeq}

mapper.sql:
FROM transport a
JOIN member b on a.member_memberSeq = b.memberSeq
WHERE 1=1
	AND delNy = #{searchDelNy}
	AND useNy = #{searchUseNy}
	AND transportDiv = #{searchDiv}

		AND transportSeq = #{searchValue}
		AND member_memberSeq = #{searchValue}
		AND b.name LIKE CONCAT('%',#{searchValue},'%')
		AND phone LIKE CONCAT('%',#{searchValue},'%')
		AND home LIKE CONCAT('%',#{searchValue},'%')
